    public function store(Request $request, $id)
    {
        $type = $request->get('type');
        $element = $this->getItem($type, $id);

        if ($request->hasFile('files')):
            $pathSociety = $element->society->name;
            $directory = $type == 'Ticket' ? '/app/SfTicket' : '/app/TaskManager';
            $appPath = public_path() . $directory . '/' . $pathSociety;

            if (!file_exists($appPath)):
                mkdir($appPath, 0777, true);
            endif;

            if (!file_exists($appPath . '/' . $element->id)):
                mkdir($appPath . '/' . $element->id, 0777, true);
            endif;
            $destinationPath = $appPath . '/' . $element->id;

            $AllFiles = [];
            $files = $request->file('files');
            foreach ($files as $file):
                $filename = str_replace(' ', '', $file->getClientOriginalName());
                $filename = $this->transliterateString($filename);
                $pj = new Attachment();
                $pj->name = $filename;
                $pj->url = $directory . '/' . $pathSociety . '/' . $element->id . '/' . $filename;
                $pj->attachable_type = 'App\\' . $request->get('type');
                $pj->attachable_id = $element->id;
                $pj->name = $filename;
                $pj->size = $file->getSize();
                $pj->type = $file->getClientOriginalExtension();
                $pj->save();
                $AllFiles[] = $pj;
                $file->move($destinationPath, '/' . $filename);
            endforeach;
            return response(['files' => $AllFiles, 'ticket' => $element]);
        endif;
    }

