    private function getItem($element, $id)
    {
        if ($element == 'Ticket') {
            return Ticket::findOrFail($id);
        } elseif ($element == 'Task') {
            return Task::findOrFail($id);
        }
    }

    public function sendEmail(Request $request)
    {
        $attachementCount = $request->attachments;
        $ticket = Ticket::findOrFail($request->get('ticket'));
        $author = Auth::user();
        $user = User::findOrFail($ticket->user_id);
        $leader = User::where('society_id', $user->society_id)->where('role', 'leader')->get();
        $this->dispatch(new newAttachmentJob($ticket, $attachementCount, $author,$user, $leader));

    }
